sys_variable_value.value:
(function execute(inputs, outputs) {
var cs = inputs.csRecord;
var mh = inputs.msgHeading;
var mg = inputs.msg;

try{
var ttMsg = new x_774159_table_t_0.TableTopUtils()._generateNotification(cs,mh,mg);
gs.info('Table Top Testing - Action' +cs);
gs.info('Table Top Testing - Action' +mh);
gs.info('Table Top Testing - Action' +mg);

outputs.test = ttMsg;


}catch(ex){
  var message = ex.message;
  gs.error('Error occurred when attempting to generate a system notification. Error: ' + message);
}


})(inputs, outputs);

