sys_variable_value.value:
(function execute(inputs, outputs) {
/*Variable Section */

var msg_icon = inputs.msgIcon;
var msg_type = inputs.msgType;
var msg_heading = inputs.msgHeading;
var msg = inputs.msg;
var route = inputs.route;
var sysId = inputs.csRecord;
var tbl = inputs.csTable;
var trigger = inputs.csTrigger;
var usr = inputs.csUser;


if (usr && sysId) {
    var now_GR = new GlideRecord("ui_notification_inbox");
    now_GR.initialize();
    now_GR.recipient = usr;
    now_GR.status = "new";
    
    var payload = {
      message_icon: msg_icon,
      message_type: msg_type,
      message_heading: msg_heading,
      message: msg,
    };
    now_GR.payload = JSON.stringify(payload);
	// Using Next Experience Notification Route
    now_GR.route = route;
    now_GR.target = sysId;
    // This table should match the table the Business Rule is watching
    now_GR.target_table = tbl;
    now_GR.triggered_by = trigger;
    now_GR.insert();
  }


})(inputs, outputs);

