sys_variable_value.value:
(function execute(inputs, outputs) {

var cn = inputs.name,
    ca = inputs.alignment.toLowerCase(),
    cj = inputs.job,
    cw = inputs.world;
    ri = inputs.record_id;


var generator = new x_774159_table_t_0.TableTopUtils()._generatePrompt(ri,cn,ca,cj,cw);
outputs.prompt = generator;
})(inputs, outputs);

